mapper.select:
select c.course_name,c.category courseCategory,c.grade_requirement courseYear,c.credit,g.*
from course c left join grade g on c.course_id = g.course_id
where g.student_id = #{studentId}

select c.course_name,c.category,c.grade_requirement courseYear,c.credit,g.*
from course c left join grade g on c.course_id = g.course_id

    student_id=#{studentId}
    and c.course_name like concat('%',#{courseName},'%')
    and c.category = #{courseCategory}
    and c.grade_requirement = #{courseYear}
    and c.credit = #{credit}

select c.category courseCategory,sum(g.final_grade * c.credit)/sum(c.credit) avgScore from course c left join grade g on c.course_id = g.course_id
and g.student_id = #{studentId}
and c.semester in
#{semester}
group by c.category order by case when c.category = '数学' then 1 when c.category = '计算机' then 2 when c.category = '电子信息' then 3 when c.category = '自然基础学科' then 4 when c.category = '思想道德' then 5 when c.category = '体育' then 6 end
